var Modbus=require('modbusrtu').Modbus;

var portID='/dev/serial/by-id/usb-FTDI_FT232R_USB_UART_AI04UGGP-if00-port0'
var modbus=new Modbus(
		portID,
		{},
		function(err){
		if (!err){
			read()
		}else
			console.error('Modbus constructor error:'+err);
		});
function read() {
modbus.readCoils(
		1,//Slave address
		0,//first coil status
		8,//number of coils to read
		function(err,data){
	if (!err) {
	console.log(data.data)
	}else{
	console.error('readCoils() error:'+err);
	}
		}
		);}
